import { useEffect , useState } from "react";
import { useNavigate } from "react-router-dom";
import * as api from '../../api/index.js';
import './home.css';
import { Card, Table } from 'react-bootstrap';
import { useOutletContext } from "react-router-dom";

const RunningCourses = () => {
    const navigate = useNavigate();
    const [data,setData] = useState([]);
    const [semesters,setSemesters] = useState([]);
    const [user,setUser] = useOutletContext();
    if (!user) {
        navigate('/login');
    }

    const getData = async () => {
        try{
            const response = await api.coursesTaken();
            setData(response.data.courses);
            return Promise.resolve(response.data.courses);
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function fetchData() {
            const datas = await getData()
            var sem = [];
            var sems = [];
            var prevsem = datas[0].semester;
            var prevyear = datas[0].year;
            for(let i=0;i<datas.length;i++){
                if(datas[i].semester !== prevsem || datas[i].year !== prevyear){
                    sems.push(sem);
                    sem = [];
                    prevsem = datas[i].semester;
                    prevyear = datas[i].year;
                }
                sem.push(datas[i]);
            }
            sems.push(sem);
            setSemesters(sems); 
        }
        fetchData();
    },[]);

    return (
        <div className="home" style={{margin:'50px 300px 50px 300px'}}>
            <div>
                <h6>Semester-wise Details</h6>
            </div>
            <div>
                {
                    semesters.map((ele,index) => {
                    return <div style={{margin:'25px 0px 25px 0px'}}><SemisterCard sem={ele} key={index}/></div>
                })}
            </div>
        </div>
    );
}

export default RunningCourses;
