import { useEffect , useState } from "react";
import { useNavigate , useParams } from "react-router-dom";
import * as api from '../../api/index.js';
import { Card, Table } from 'react-bootstrap';
import { useOutletContext, Link } from "react-router-dom";

const Instructor = () => {
    const navigate = useNavigate();
    const [data,setData] = useState({});
    const user = useOutletContext();
    const instid = useParams();
    if (!user.user) {
        navigate('/login');
    }

    const getData = async (instid) => {
        try{
            const response = await api.getInstructor(instid);
            setData(response.data);
            console.log(response.data);
            return Promise.resolve(response.data);
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function fetchData() {
            await getData(instid)
        }
        fetchData();
    },[instid]);

    return (
        <div className="running_courses" style={{margin:'50px 300px 50px 300px'}}>
            <div style={{margin:'25px 0px 25px 0px'}}><SemisterCard inst={data}/></div>
        </div>
    );
}

export default Instructor;

function SemisterCard(props) {
    return !props.inst ? null : (
    <Card>
      <Card.Header>
        <h6>Course Id : </h6>
        <h6>Course Title : </h6>
        </Card.Header>
      <Card.Body>
        <Table hover>
            <thead>
                <tr>
                    <th>Credits</th>
                    <th>Section</th>
                    <th>Instructor</th>
                </tr>
            </thead>
            <tbody>
                {
                    // props.course.map((ele,index) => {
                    //     return (
                    //         <tr key={index}>
                    //             <td>{ele.credits}</td>
                    //             <td>{ele.sec_id}</td>
                    //             <td><Link to={`/instructor/${ele.id}`}>{ele.name}</Link></td>
                    //         </tr>
                    //     )
                    // })
                }
            </tbody>
        </Table>
      </Card.Body>
    </Card>
    );
}
