import { useEffect , useState } from "react";
import { useNavigate } from "react-router-dom";
import * as api from '../../api/index.js';
import { Card, Table } from 'react-bootstrap';
import { useOutletContext, Link } from "react-router-dom";
import {ReactSearchAutocomplete} from 'react-search-autocomplete'

const Registration = () => {
    const navigate = useNavigate();
    const [data,setData] = useState([]);
    const user = useOutletContext();
    if (!user.user) {
        navigate('/login');
    }

    const items = [
        {
          id: 0,
          name: 'Cobol'
        },
        {
          id: 1,
          name: 'JavaScript'
        },
        {
          id: 2,
          name: 'Basic'
        },
        {
          id: 3,
          name: 'PHP'
        },
        {
          id: 4,
          name: 'Java'
        }
      ]

    const getData = async () => {
        try{
            const response = await api.getAllRunningCourses();
            setData(response.data.courses);
            return Promise.resolve(response.data.courses);
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function fetchData() {
            await getData()
        }
        fetchData();
    },[]);

    const handleOnSearch = (string, results) => {
        // onSearch will have as the first callback parameter
        // the string searched and for the second the results.
        console.log(string, results)
      }

      const handleOnHover = (result) => {
        // the item hovered
        console.log(result)
      }

      const handleOnSelect = (item) => {
        // the item selected
        console.log(item)
      }

      const handleOnFocus = () => {
        console.log('Focused')
      }

      const formatResult = (item) => {
        return (
          <>
            <span style={{ display: 'block', textAlign: 'left' }}>id: {item.id}</span>
            <span style={{ display: 'block', textAlign: 'left' }}>name: {item.name}</span>
          </>
        )
      }

    return (
        <div className="running_courses" style={{margin:'50px 300px 50px 300px'}}>
            <ReactSearchAutocomplete
            items={items}
            onSearch={handleOnSearch}
            onHover={handleOnHover}
            onSelect={handleOnSelect}
            onFocus={handleOnFocus}
            autoFocus
            formatResult={formatResult}
            />
            <div style={{margin:'25px 0px 25px 0px',zIndex:'0'}}><SemisterCard courses={data}/></div>
        </div>
    );
}

export default Registration;

function SemisterCard(props) {
    return (
    <Card  className="text-center">
      <Card.Header><h6>Registered courses</h6></Card.Header>
      <Card.Body>
        <Table hover>
            <thead>
                <tr>
                    <th>Course Id</th>
                    <th>Course Title</th>
                </tr>
            </thead>
            <tbody>
                {
                    props.courses.map((ele,index) => {
                        return (
                            <tr key={index}>
                                <td><Link to={`/course/${ele.courseid}`}>{ele.courseid}</Link></td>
                                <td>{ele.title}</td>
                            </tr>
                        )
                    })
                }
            </tbody>
        </Table>
      </Card.Body>
    </Card>
    );
}
