import './App.css';
import LoginPage from './components/login/login';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Routes, Route } from 'react-router-dom';
import { useState } from 'react';
import Dashboard from './components/dashboard/dashboard';
import HomePage from './components/home/home';
import Registration from './components/registration/registration';
import Course from './components/courses/course';
import RunningCourses from './components/running_courses/running_courses';
import Instructors from './components/instructors/instructors';
import RunningCourse from './components/running_courses/running_course/running_course';
import Instructor from './components/instructors/instructor/instructor';
import ErrorPage from './ErrorPage';
import { useEffect } from 'react';
import * as api from './api/index.js';
import { useNavigate } from 'react-router-dom';


function App() {
  const [user, setUser] = useState({});
  const navigate = useNavigate();
  useEffect(() => {
    const getUser = async () => {
        try{
            const response = await api.getUser();
            if (response.status === 200) {
                setUser(response.data.user);
                return ;
            }
        }
        catch (error) {
            console.log(error);
            navigate('/login');
            return;
        }
    }
    getUser();
  });

  const handleLogout = async (event) => {
    try {
        const response = await api.logout();
        if (response.status === 200) {
            setUser({});
            navigate('/login');
            return;
        }
    }
    catch (error) {
        console.log(error);
    }
  }

  return (
      <div className="App">
        <Routes>
          <Route element={<Dashboard user={user} setUser={setUser} handleLogout={handleLogout}/>}>
            <Route path="/home" element={<HomePage user={user} setUser={setUser}/>} />
            <Route path="/home/registration" element={<Registration />} />
            <Route path="/course/:id" element={<Course />} />
            <Route path="/course/running/:dept_name" element={<RunningCourse />} />
            <Route path="/course/running" element={<RunningCourses />} />
            <Route path="/instructor" element={<Instructors />} />
            <Route path="/instructor/:id" element={<Instructor />} />
          </Route>
          <Route path="/login" element={<LoginPage user={user} setUser={setUser} />} />
          <Route path="*" element={<ErrorPage />} />
        </Routes>
      </div>
    );
}

export default App;
