import { useEffect , useState , useRef} from "react";
import { useNavigate } from "react-router-dom";
import * as api from '../../api/index.js';
import './home.css';
import { Card, Button , Table} from 'react-bootstrap';

const HomePage = (props) => {
    const navigate = useNavigate();
    const [data,setData] = useState([]);
    const user = useRef(props.user);

    const getData = async () => {
        try{
            const response = await api.coursesTaken();
            setData(response.data.courses);
            return ;
        }
        catch (error) {
            console.log(error);
        }
    }
    useEffect(() => {
        const checkLogin = async () => {
            try{
                const response = await api.checkLogin();
                console.log(response);
                if (response.status === 200) {
                    console.log('Logged in');
                    return ;
                }
                else {
                    console.log('Not logged in');
                    user.current = {}
                    navigate('/login');
                    return;
                }
            }
            catch (error) {
                console.log(error);
                user.current = {}
                navigate('/login');
                return;
            }
        }
        checkLogin();
    });

    useEffect(() => {
        getData();
    },[]);

    const handleLogout = async (event) => {
        try {
            const response = await api.logout();
            console.log(response);
            if (response.status === 200) {
                console.log('Logout successful');
                setData([]);
                user.current = {}
                navigate('/login');
                return;
            }
            else {
                console.log('Logout failed');
            }
        }
        catch (error) {
            console.log(error);
        }
    }
    console.log(user.current);
    return (
        <div className="home">
            <UserCard user={user.current}/>
            {data.map(({sem}) => {
                return <SemisterCard sem={sem}/>
            })}
        <button onClick={() => {handleLogout()}}>Logout</button>
        </div>
    );
}

export default HomePage;

function UserCard(props) {
    console.log(props);
    return (
        <div>
    <Card>
      <Card.Header>
      <Table hover>
      <thead>
        <tr>
          <th>{props.user.username}</th>
          <th>Last Name</th>
        </tr>
      </thead>
    </Table>
      </Card.Header>
      <Card.Body>
      <Table hover>
      <tbody>
        <tr>
          <th>First Name</th>
          <th>Last Name</th>
        </tr>
      </tbody>
    </Table>
      </Card.Body>
    </Card>
    </div>
    );
}

function SemisterCard(props) {
    return (
    <Card>
      <Card.Header>Featured</Card.Header>
      <Card.Body>
        <Card.Title>Special title treatment</Card.Title>
        <Card.Text>
          With supporting text below as a natural lead-in to additional content.
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
    );
}
