import {Nav} from 'react-bootstrap';
import { Outlet } from 'react-router-dom';
import { LinkContainer } from 'react-router-bootstrap';
import { Card, Table ,CardGroup} from 'react-bootstrap';
import { useEffect , useState } from 'react';
import * as api from '../../api/index.js';
import { useNavigate } from 'react-router-dom';


const Dashboard = () => {
  const [user,setUser] = useState({});
  const navigate = useNavigate();
  useEffect(() => {
    const getUser = async () => {
        try{
            const response = await api.getUser();
            if (response.status === 200) {
                setUser(response.data);
                return ;
            }
        }
        catch (error) {
            console.log(error);
            navigate('/login');
            return;
        }
    }
    getUser();
  },[navigate]);

  const handleLogout = async (event) => {
    try {
        const response = await api.logout();
        if (response.status === 200) {
            setUser({});
            navigate('/login');
            return;
        }
    }
    catch (error) {
        console.log(error);

    }
  }
    return (
        <>
        <div className='header row' >
        <div className='column'>
          <Asc user={user}/>
        </div>
        <div className='column'>
        <UserCard user={user}/>
        </div>
        </div>
        <Navbar handleLogout={handleLogout}/>
        <Outlet context={[user,setUser]}/>
        </>
    )
}

export default Dashboard;



function Navbar(props) {
  return (
    <>
      <Nav className="justify-content-center" fill variant="tabs">
        <Nav.Item>
          <LinkContainer to="/home"><Nav.Link>Home</Nav.Link></LinkContainer>
        </Nav.Item>
        <Nav.Item>
          <LinkContainer to="/home/registration"><Nav.Link>Register</Nav.Link></LinkContainer>
        </Nav.Item>
        <Nav.Item>
          <LinkContainer to="/course/running"><Nav.Link>Running Courses</Nav.Link></LinkContainer>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link onClick={props.handleLogout}>Logout</Nav.Link>
        </Nav.Item>
      </Nav>
    </>
  );
}

function UserCard(props) {
  return (
      <div>
  <Card>
    <Card.Body>
    <Table >
    <tbody>
      <tr>
        <th>Student ID : {props.user.userId}</th>
        <th>Username : {props.user.username}</th>
      </tr>
      <tr>
        <th>Department : {props.user.department}</th>
        <th>Total Credits : {props.user.totalCredits}</th>
      </tr>
    </tbody>
  </Table>
    </Card.Body>
  </Card>
  </div>
  );
}

function Asc (props) {
  return (
    <div>
<Card>
  <Card.Body>
  <Table >
  <tbody>
    <tr>
      <th>Student ID : {props.user.userId}</th>
      <th>Username : {props.user.username}</th>
    </tr>
    <tr>
      <th>Department : {props.user.department}</th>
      <th>Total Credits : {props.user.totalCredits}</th>
    </tr>
  </tbody>
</Table>
  </Card.Body>
</Card>
</div>
);
}


function Header() {
  return (
    <CardGroup>
      <Card>
        <Card.Img variant="top" src="holder.js/100px160" />
        <Card.Body>
          <Card.Title>Card title</Card.Title>
          <Card.Text>
            This is a wider card with supporting text below as a natural lead-in
            to additional content. This content is a little bit longer.
          </Card.Text>
        </Card.Body>
        <Card.Footer>
          <small className="text-muted">Last updated 3 mins ago</small>
        </Card.Footer>
      </Card>
      <Card>
        <Card.Img variant="top" src="holder.js/100px160" />
        <Card.Body>
          <Card.Title>Card title</Card.Title>
          <Card.Text>
            This card has supporting text below as a natural lead-in to
            additional content.{' '}
          </Card.Text>
        </Card.Body>
        <Card.Footer>
          <small className="text-muted">Last updated 3 mins ago</small>
        </Card.Footer>
      </Card>
      <Card>
        <Card.Img variant="top" src="holder.js/100px160" />
        <Card.Body>
          <Card.Title>Card title</Card.Title>
          <Card.Text>
            This is a wider card with supporting text below as a natural lead-in
            to additional content. This card has even longer content than the
            first to show that equal height action.
          </Card.Text>
        </Card.Body>
        <Card.Footer>
          <small className="text-muted">Last updated 3 mins ago</small>
        </Card.Footer>
      </Card>
    </CardGroup>
  );
}
