import {Nav} from 'react-bootstrap';
import { Outlet } from 'react-router-dom';
import { LinkContainer } from 'react-router-bootstrap';
import { Card, Table } from 'react-bootstrap';


const Dashboard = (props) => {

  const navigate = useNavigate();
  useEffect(() => {
    const getUser = async () => {
        try{
            const response = await api.getUser();
            if (response.status === 200) {
                setUser(response.data.user);
                return ;
            }
        }
        catch (error) {
            console.log(error);
            navigate('/login');
            return;
        }
    }
    getUser();
  },[navigate]);

  const handleLogout = async (event) => {
    try {
        const response = await api.logout();
        if (response.status === 200) {
            props.setUser({});
            navigate('/login');
            return;
        }
    }
    catch (error) {
        console.log(error);
    }
  }
    console.log(props);
    return (
        <>
        <UserCard user={props.user}/>
        <Navbar handleLogout={handleLogout}/>
        <Outlet/>
        </>
    )
}

export default Dashboard;



function Navbar(props) {
  return (
    <>
      <Nav className="justify-content-center" fill variant="tabs">
        <Nav.Item>
          <LinkContainer to="/home"><Nav.Link>Home</Nav.Link></LinkContainer>
        </Nav.Item>
        <Nav.Item>
          <LinkContainer to="/home/registration"><Nav.Link>Register</Nav.Link></LinkContainer>
        </Nav.Item>
        <Nav.Item>
          <LinkContainer to="/course/running"><Nav.Link>Running Courses</Nav.Link></LinkContainer>
        </Nav.Item>
        <Nav.Item>
          <Nav.Link onClick={props.handleLogout}>Logout</Nav.Link>
        </Nav.Item>
      </Nav>
    </>
  );
}

function UserCard(props) {
  console.log(props);
  return (
      <div>
  <Card>
    <Card.Header>
    <Table hover>
    <thead>
      <tr>
        <th>{props.user.username}</th>
        <th>Last Name</th>
      </tr>
    </thead>
  </Table>
    </Card.Header>
    <Card.Body>
    <Table hover>
    <tbody>
      <tr>
        <th>First Name</th>
        <th>Last Name</th>
      </tr>
    </tbody>
  </Table>
    </Card.Body>
  </Card>
  </div>
  );
}
