import './App.css';
import LoginPage from './components/login/login';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import HomePage from './components/home/home';
import ErrorPage from './ErrorPage';
import { useState } from 'react';


function App() {
  const [user, setUser] = useState({});
  const [loggedIn, setLoggedIn] = useState(false);

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route exact path="/" element={loggedIn? <Navigate to='/home'/> : <Navigate to='/login'/>}/>
          <Route exact path="/login" element={<LoginPage loggedIn user setUser setLoggedIn/>}/>
          <Route exact path="/home" element={ <HomePage user setUser setLoggedIn/>}/>
          <Route exact path="*" element={<ErrorPage />}/>
        </Routes>
      </div>
    </Router>
  );
}

export default App;
