import { useEffect , useState } from "react";
import { useNavigate } from "react-router-dom";
import * as api from '../../api/index.js';
import './home.css';
import { Card, Button, Table } from 'react-bootstrap';
import { useOutletContext } from "react-router-dom";

const HomePage = () => {
    const navigate = useNavigate();
    const [data,setData] = useState([]);
    const [semesters,setSemesters] = useState([]);
    const [user,setUser] = useOutletContext();
    if (!user) {
        navigate('/login');
    }

    const getData = async () => {
        try{
            const response = await api.coursesTaken();
            setData(response.data.courses);
            return data;
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function fetchData() {
            await getData().then((datas) => {
                var sem = [];
                var sems = [];
                console.log(datas);
                var prevsem = datas[0].semester;
                var prevyear = datas[0].year;
                for(let i=0;i<datas.length;i++){
                    if(datas[i].semester !== prevsem || datas[i].year !== prevyear){
                        sems.push(sem);
                        sem = [];
                        prevsem = datas[i].semester;
                        prevyear = datas[i].year;
                    }
                    sem.push(datas[i]);
                }
                setSemesters(sems);
            }).catch((error) => {
                console.log(error);
            });
        }
        fetchData();
    },[]);

    return (
        <div className="home">
            {
                semesters.map((ele,index) => {
                return <SemisterCard sem={ele} key={index}/>
            })}
        </div>
    );
}

export default HomePage;

function SemisterCard(props) {
    return (
    <Card>
      <Card.Header>{props.sem[0].semester} {props.sem[0].year}</Card.Header>
      <Card.Body>
        <Table striped bordered hover>
            <thead>
                <tr>
                    <th>Course ID</th>
                    <th>Title</th>
                    <th>Credits</th>
                    <th>Grade</th>
                </tr>
            </thead>
            <tbody>
                {
                    props.sem.map((ele) => {
                        return (
                            <tr>
                                <td>{ele.course_id}</td>
                                <td>{ele.title}</td>
                                <td>{ele.credits}</td>
                                <td>{ele.grade}</td>
                            </tr>
                        )
                    })
                }
            </tbody>
        </Table>
      </Card.Body>
    </Card>
    );
}
