import { useEffect , useState } from "react";
import { useNavigate } from "react-router-dom";
import * as api from '../../api/index.js';
import { Card, Table } from 'react-bootstrap';
import { useOutletContext } from "react-router-dom";

const RunningCourses = () => {
    const navigate = useNavigate();
    const [data,setData] = useState([]);
    // const [semesters,setSemesters] = useState([]);
    const [user,setUser] = useOutletContext();
    if (!user) {
        navigate('/login');
    }

    const getData = async () => {
        try{
            const response = await api.getRunningCourses();
            setData(response.data.department);
            return Promise.resolve(response.data.department);
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function fetchData() {
            const datas = await getData()
        }
        fetchData();
    },[]);

    return (
        <div className="running_courses" style={{margin:'50px 300px 50px 300px'}}>
            <div>
                <h6>Semester-wise Details</h6>
            </div>
            <div style={{margin:'25px 0px 25px 0px'}}><SemisterCard dept={data}/></div>
        </div>
    );
}

export default RunningCourses;

function SemisterCard(props) {
    return (
    <Card  className="text-center">
      <Card.Header>{props.sem[0].semester} {props.sem[0].year}</Card.Header>
      <Card.Body>
        <Table hover>
            <thead>
                <tr>
                    <th>Department</th>
                </tr>
            </thead>
            <tbody>
                {
                    props.sem.map((ele,index) => {
                        return (
                            <tr key={index}>
                                <td>{ele.course_id}</td>
                                <td>{ele.title}</td>
                                <td>{ele.credits}</td>
                                <td>{ele.grade}</td>
                            </tr>
                        )
                    })
                }
            </tbody>
        </Table>
      </Card.Body>
    </Card>
    );
}
