import { useEffect , useState } from "react";
import { useNavigate , useParams } from "react-router-dom";
import * as api from '../../api/index.js';
import { Card, Table } from 'react-bootstrap';
import { useOutletContext, Link } from "react-router-dom";

const Course = () => {
    const navigate = useNavigate();
    const [data,setData] = useState([]);
    const user = useOutletContext();
    const courseid = useParams();
    if (!user.user) {
        navigate('/login');
    }

    const getData = async (courseid) => {
        try{
            const response = await api.getCourse(courseid);
            setData(response.data.courses);
            return Promise.resolve(response.data.courses);
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function fetchData() {
            await getData(courseid)
        }
        fetchData();
    },[courseid]);

    return (
        <div className="running_courses" style={{margin:'50px 300px 50px 300px'}}>
            <div style={{margin:'25px 0px 25px 0px'}}><SemisterCard course={data}/></div>
        </div>
    );
}

export default Course;

function SemisterCard(props) {
    console.log(props.course);
    return !props.course[0] ? null : (
    <Card>
      <Card.Header>
        <h6>Course Id : {props.course[0].course_id}</h6>
        <h6>Course Title : {props.course[0].title}</h6>
        </Card.Header>
      <Card.Body>
        <Table hover>
            <thead>
                <tr>
                    {/* <th>Course Id</th>
                    <th>Course Title</th> */}
                    <th>Section</th>
                    <th>Instructor</th>
                </tr>
            </thead>
            <tbody>
                {
                    props.course.map((ele,index) => {
                        return (
                            <tr key={index}>
                                {/* <td><Link to={`/course/${ele.course_id}`}>{ele.courseid}</Link></td>
                                <td>{ele.title}</td> */}
                                <td>{ele.sec_id}</td>
                                <td><Link to={`/instructor/${ele.id}`}>{ele.name}</Link></td>
                            </tr>
                        )
                    })
                }
            </tbody>
        </Table>
      </Card.Body>
    </Card>
    );
}
