import { useEffect , useState } from "react";
import { useNavigate } from "react-router-dom";
import * as api from '../../api/index.js';
import { Button, Card, Table } from 'react-bootstrap';
import { useOutletContext } from "react-router-dom";
import {ReactSearchAutocomplete} from 'react-search-autocomplete'

const Registration = () => {
    const navigate = useNavigate();
    const [data,setData] = useState([]);
    const [register,setRegister] = useState([]);
    const [course,setCourse] = useState([]);
    const [coursedrop,setCoursedrop] = useState([]);
    const [registered,setRegistered] = useState([]);
    const user = useOutletContext();
    if (!user.user) {
        navigate('/login');
    }

    const getData = async () => {
        try{
            const response = await api.getAllRunningCourses();
            setData(response.data.courses.map((obj,i) => ({id: i ,...obj})));
            return Promise.resolve(response.data.courses);
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function fetchData() {
            await getData();
        }
        fetchData();
    },[]);

    const registerData = async (item) => {
        try{
            const response = await api.registerCourses({user:user.user.userId,courseid:item.courseid,sec_id:item.sec_id,semester:item.semester,year:item.year});
            setRegister(register.filter((obj) => obj.id !== item.id));
            setRegistered([...registered,item]);
            return Promise.resolve(response.data.courses);
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function regData() {
            await registerData(course);
        }
        regData();
    },[course]);

    const dropCourse = async (item) => {
        try{
            const response = await api.dropCourses({user:user.user.userId,course_id:item.courseid,sec_id:item.sec_id,semester:item.semester,year:item.year});
            setRegister(registered.filter((obj) => obj.id !== item.id));
            return Promise.resolve(response.data.courses);
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        async function dropData() {
            await dropCourse(coursedrop);
        }
        dropData();
    },[coursedrop]);

    const handleOnSearch = (string, results) => {
        console.log(string, results, 'onSearch')
      }

      const handleOnHover = (result) => {
        console.log(result, 'onHover')
      }

      const handleOnSelect = (item) => {
        setRegister([...register,item]);
        console.log(item, 'onSelect')
      }

      const handleOnFocus = () => {
        console.log('Focused')
      }

      const handleDelete = (item) => {
        setRegister(register.filter((obj) => obj.id !== item.id));
        }

        const handleRegister = (item) => {
            setCourse(item);
        }

        const handleDrop = (item) => {
            setCoursedrop(item);
        }



      const formatResult = (item) => {
        return (
          <>
            <span style={{ display: 'block', textAlign: 'left' }}>id: {item.courseid} title: {item.title} section: {item.sec_id}</span>
          </>
        )
      }
      console.log(register, 'register');
      console.log(registered, 'registered');
      console.log(course, 'course');
        console.log(coursedrop, 'coursedrop');
    return (
        <div className="running_courses" style={{margin:'50px 300px 50px 300px'}}>
            <ReactSearchAutocomplete
            fuseOptions={{ keys: ["title","courseid"] }}
            items={data}
            resultStringKeyName="title"
            onSearch={handleOnSearch}
            onHover={handleOnHover}
            onSelect={handleOnSelect}
            onFocus={handleOnFocus}
            autoFocus
            styling={{ zIndex: 1 }} 
            formatResult={formatResult}
            />
            {!register[0] ? null : (<Table>
                <thead>
                    <tr>
                        <th>Course Id</th>
                        <th>Course Title</th>
                        <th>Section</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        register.map((ele,index) => {
                            return (
                                <tr key={index}>
                                    <td>{ele.courseid}</td>
                                    <td>{ele.title}</td>
                                    <td>{ele.sec_id}</td>
                                    <td><Button onClick={() => handleRegister(ele)}>Register</Button></td>
                                    <td><Button onClick={() => handleDelete(ele)}>Delete</Button></td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </Table>)}
            <div style={{margin:'25px 0px 25px 0px'}}><SemisterCard courses={registered} handleDrop={handleDrop}/></div>
        </div>
    );
}

export default Registration;

function SemisterCard(props) {
    console.log(props, 'props');

    return (
    <Card  className="text-center">
      <Card.Header><h6>Registered courses</h6></Card.Header>
      <Card.Body>
        {!props.courses[0] ? null:(<Table hover>
                <thead>
                    <tr>
                        <th>Course Id</th>
                        <th>Course Title</th>
                        <th>Section</th>
                    </tr>
                </thead>
                <tbody>
                <tr>
                    {props.courses.map((ele,index) => {
                        return (
                            <tr key={index}>
                                <td>{ele.courseid}</td>
                                <td>{ele.title}</td>
                                <td>{ele.sec_id}</td>
                                <td><Button onClick={() => props.handleDrop(ele)}>Drop</Button></td>
                            </tr>
                        )
                    })}
                </tr>
                </tbody>
        </Table>)}
      </Card.Body>
    </Card>
    );
}
