import { useEffect , useState } from "react";
import { useNavigate } from "react-router-dom";
import * as api from '../../api/index.js';
import './home.css';
import { Card, Button } from 'react-bootstrap';
import { useOutletContext } from "react-router-dom";

const HomePage = () => {
    const navigate = useNavigate();
    const [data,setData] = useState([]);
    const [semesters,setSemesters] = useState([]);
    const [user,setUser] = useOutletContext();
    if (!user) {
        navigate('/login');
    }

    const getData = async () => {
        try{
            const response = await api.coursesTaken();
            setData(response.data.courses);
            return ;
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        getData();
        var sem = [];
        var semester = [];
        var prevsem = data[0].semester;
        var prevyear = data[0].year;
        for(let i=0;i<data.length;i++){
            if(data[i].semester !== prevsem || data[i].year !== prevyear){
                semester.push(sem);
                sem = [];
                prevsem = data[i].semester;
                prevyear = data[i].year;
            }
            sem.push(data[i]);
        }
        setSemesters(semester);
    },[]);

    return (
        <div className="home">
            {
                semesters.map(({ele}) => {
                return <SemisterCard sem={ele}/>
            })}
        </div>
    );
}

export default HomePage;

function SemisterCard(props) {
    return (
    <Card>
      <Card.Header></Card.Header>
      <Card.Body>
        <Card.Title>Special title treatment</Card.Title>
        <Card.Text>
          With supporting text below as a natural lead-in to additional content.
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
    );
}
